import styled from 'styled-components';

const StyledAvatar = styled.div`
    position: relative;
`;

const StatusIcon = styled.div`
    position: absolute;
    left: 2px;
    top: 4px;

    &::before {
        content: "";
        display: block;
        position: absolute;
        width: 16px;
        height: 16px;
        border-radius: 50%;
        background-color: white;
    }

    &::after {
        content: "";
        display: block;
        position: absolute;
        width: 8px;
        height: 8px;
        border-radius: 50%;
        background-color: green;
    }
`;

const AvatarClip = styled.div`
    width: 48px;
    height: 48px;
    border-radius: 50%;
    overflow: hidden;
`;

const AvatarImage = styled.img`
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
`;

export default StyledAvatar;
export { StatusIcon, AvatarClip, AvatarImage };
