import { css } from "styled-components";

export const circle = (color, size = "8px") => css`
    width: ${size};
    height: ${size};
    border-radius: 50%;
    background-color: ${color};
`;

export const activeBar = ({ barWidth = "8px", shadowWidth = "20px" } = {}) => css`
    position: relative;
    &::before, &::after {
        display: block;
        content: "";
        position: absolute;
        height: 100%;
        left: 0;
    }

    &::before {
        width: ${barWidth};
        bac
    }
`;
